'use strict';

const express = require('express');
const bodyParser = require('body-parser');
const app = express();
const router = express.Router();

// load routes
const indexRoute = require('./routes/index');


app.use(bodyParser.json());
app.use(bodyParser.urlencoded({ extended: false}));

// IDEA: Recursos
app.use('/',indexRoute);

module.exports = app;
